import React, { Component } from 'react';
import styled from 'styled-components';
import {Link} from 'react-router-dom';
import {ProductConsumer} from '../context';

class Product extends Component {
    render() {
        const {id, name, image, price, inCart} = this.props.product;
        return (
            <ProductWrapper className="col-9 mx-auto col-md-6 col-lg-3 my-3">
                <div className="card">
                    <div className="img-cntainer p-5" onClick={() => console.log('You clicked me')}>
                        <Link to="/details">
                            <img src={image} alt="product" className="card-img-top"/>
                        </Link>
                        <button className="cart-btn" disabled={inCart?true:false} onClick={()=>{console.log('added to the cart');}}>
                            {inCart?(<p className="text-capitalize mb-0" disabled>in cart</p>):(<i className="fas fa-cart-plus"/>)}
                        </button>
                        
                    </div>
                </div>
            </ProductWrapper>
        );
    }
}

const ProductWrapper = styled.div`

`;

export default Product;
